mapper.update:
update member set
	member_Addr = #{memberAddr},
	member_mail = #{memberMail},
	member_phone = #{memberPhone}
where member_no = #{memberNo}

mapper.select:
select member_no as memberNo, member_id as memberId, member_pw as memberPw, member_name as memberName, member_addr as memberAddr, member_phone as memberPhone, member_mail as memberMail, member_grade as memberGrade, member_mileage as memberMileage, kakao from member
where member_id = #{memberId}
where member_pw = #{memberPw}

select member_no as memberNo, member_id as memberId, member_pw as memberPw, member_name as memberName, member_addr as memberAddr, member_phone as memberPhone, member_mail as memberMail, member_grade as memberGrade, member_mileage as memberMileage, kakao from member where member_name = #{memberName} and member_grade = #{memberGrade}
and member_mail = #{memberMail}
and member_phone = #{memberPhone}

mapper.update:
update member
set member_pw = #{memberPw}
where member_id = #{memberId}
